      --ink: rgba(255,255,255,.88);
      --muted: rgba(255,255,255,.68);
      --faint: rgba(255,255,255,.48);
      --edge: rgba(255,255,255,.12);
      --glow: rgba(98, 210, 255, .18);
      --accent: #7dd3fc;
      --accent2: #a78bfa;
      --shadow: 0 18px 70px rgba(0,0,0,.55);
      --radius: 18px;
      --max: 78ch;
      --mono: ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;
      --serif: ui-serif, "Iowan Old Style", "Palatino Linotype", Palatino, "Garamond", Georgia, serif;
      --sans: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, "Apple Color Emoji","Segoe UI Emoji";
      --leading: 1.72;
      --p: 1.06rem;
    }
    :root[data-theme="light"]{
      --bg: #f7f8fb;
      --panel: rgba(15, 23, 42, .05);
      --ink: rgba(15, 23, 42, .92);
      --muted: rgba(15, 23, 42, .68);
      --faint: rgba(15, 23, 42, .46);
      --edge: rgba(15, 23, 42, .12);
      --glow: rgba(14, 165, 233, .12);
      --accent: #0ea5e9;
      --accent2: #7c3aed;
      --shadow: 0 18px 70px rgba(2,6,23,.12);
    }

    *{ box-sizing: border-box; }
    html, body{ height: 100%; }
    body{
      margin: 0;
      background:
        radial-gradient(1100px 700px at 18% 10%, var(--glow), transparent 55%),
        radial-gradient(900px 620px at 88% 16%, rgba(167,139,250,.12), transparent 60%),
        radial-gradient(900px 700px at 40% 110%, rgba(125,211,252,.10), transparent 60%),
        var(--bg);
      color: var(--ink);
      font-family: var(--sans);
      letter-spacing: 0.1px;
    }

    /* Top bar */
    header{
      position: sticky;
      top: 0;
      z-index: 50;
      backdrop-filter: blur(14px);
      background: linear-gradient(to bottom, rgba(0,0,0,.40), rgba(0,0,0,0));
      border-bottom: 1px solid transparent;
    }
    :root[data-theme="light"] header{
      background: linear-gradient(to bottom, rgba(247,248,251,.82), rgba(247,248,251,0));
    }
    .topbar{
      max-width: 1120px;
      margin: 0 auto;
      padding: 14px 16px;
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 14px;
    }
    .brand{
      display: flex;
      align-items: center;
      gap: 10px;
      min-width: 240px;
    }
    .sigil{
      width: 34px;
      height: 34px;
      border-radius: 12px;
      background:
        radial-gradient(circle at 30% 30%, rgba(125,211,252,.95), rgba(167,139,250,.55) 55%, rgba(255,255,255,.06) 70%),
        rgba(255,255,255,.06);
      border: 1px solid var(--edge);
      box-shadow: 0 10px 28px rgba(0,0,0,.25);
      position: relative;
      overflow: hidden;
    }
    .sigil:after{
      content:"";
      position:absolute;
      inset:-40%;
      background: conic-gradient(from 190deg, transparent, rgba(255,255,255,.18), transparent);
      animation: spin 10s linear infinite;
      opacity:.75;
    }
    @keyframes spin{ to{ transform: rotate(360deg);} }

    .brand h1{
      font-size: 0.95rem;
      margin: 0;
      letter-spacing: .4px;
      font-weight: 650;
      color: var(--ink);
      line-height: 1.15;
    }
    .brand .sub{
      display:block;
      margin-top: 2px;
      font-size: .78rem;
      color: var(--muted);
      font-weight: 520;
    }

    .controls{
      display: flex;
      gap: 10px;
      align-items: center;
      flex-wrap: wrap;
      justify-content: flex-end;
    }
    .pill{
      display: inline-flex;
      align-items: center;
      gap: 10px;
      padding: 10px 12px;
      border-radius: 999px;
      border: 1px solid var(--edge);
      background: rgba(255,255,255,.05);
      box-shadow: 0 12px 34px rgba(0,0,0,.20);
    }
    :root[data-theme="light"] .pill{ background: rgba(255,255,255,.65); }

    .btn{
      appearance: none;
      border: 1px solid var(--edge);
      background: rgba(255,255,255,.06);
      color: var(--ink);
      border-radius: 999px;
      padding: 10px 12px;
      font-weight: 650;
      cursor: pointer;
      transition: transform .08s ease, background .2s ease, border-color .2s ease;
      user-select: none;
    }
    .btn:hover{ background: rgba(255,255,255,.09); border-color: rgba(255,255,255,.18); }
    .btn:active{ transform: translateY(1px); }
    .btn:focus{ outline: 2px solid rgba(125,211,252,.30); outline-offset: 2px; }

    .label{
      font-size: .78rem;
      color: var(--muted);
      font-weight: 650;
      letter-spacing: .25px;
      white-space: nowrap;
    }
    input[type="range"]{
      width: 150px;
      accent-color: var(--accent);
    }

    /* Layout */
    main{
      max-width: 1120px;
      margin: 0 auto;
      padding: 22px 16px 80px;
      display: grid;
      grid-template-columns: 320px 1fr;
      gap: 18px;
      align-items: start;
    }
    @media (max-width: 980px){
      main{
        grid-template-columns: 1fr;
      }
      .sidebar{
        position: relative !important;
        top: auto !important;
      }
    }

    .sidebar{
      position: sticky;
      top: 76px;
      padding: 16px;
      border: 1px solid var(--edge);
      border-radius: var(--radius);
      background: linear-gradient(180deg, rgba(255,255,255,.06), rgba(255,255,255,.03));
      box-shadow: var(--shadow);
      overflow: hidden;
    }
    :root[data-theme="light"] .sidebar{
      background: linear-gradient(180deg, rgba(255,255,255,.75), rgba(255,255,255,.55));
    }
    .sidebar:before{
      content:"";
      position:absolute;
      inset:-1px;
      pointer-events:none;
      border-radius: var(--radius);
      background: radial-gradient(900px 220px at 10% 0%, rgba(125,211,252,.16), transparent 60%);
      opacity:.9;
    }
    .sidebar > *{ position: relative; }

    .toc-title{
      margin: 0 0 10px 0;
      font-size: .86rem;
      letter-spacing: .35px;
      color: var(--muted);
      font-weight: 800;
      text-transform: uppercase;
    }
    .meta{
      display: grid;
      gap: 10px;
      margin-bottom: 14px;
      padding-bottom: 14px;
      border-bottom: 1px solid var(--edge);
    }
    .chiprow{
      display:flex;
      flex-wrap: wrap;
      gap: 8px;
    }
    .chip{
      display:inline-flex;
      align-items:center;
      gap:8px;
      padding: 7px 10px;
      border: 1px solid var(--edge);
      border-radius: 999px;
      background: rgba(255,255,255,.05);
      color: var(--muted);
      font-size: .78rem;
      font-weight: 650;
    }
    :root[data-theme="light"] .chip{ background: rgba(255,255,255,.75); }

    .toc a{
      display:block;
      padding: 9px 10px;
      margin: 4px 0;
      text-decoration: none;
      border-radius: 12px;
      border: 1px solid transparent;
      color: var(--ink);
      font-weight: 650;
      font-size: .92rem;
      line-height: 1.25;
      transition: background .2s ease, border-color .2s ease;
    }
    .toc a small{
      display:block;
      color: var(--muted);
      font-weight: 600;
      font-size: .78rem;
      margin-top: 2px;
    }
    .toc a:hover{
      background: rgba(255,255,255,.06);
      border-color: var(--edge);
    }

    /* Article */
    article{
      border: 1px solid var(--edge);
      border-radius: var(--radius);
      background: linear-gradient(180deg, rgba(255,255,255,.06), rgba(255,255,255,.03));
      box-shadow: var(--shadow);
      overflow: hidden;
    }
    :root[data-theme="light"] article{
      background: linear-gradient(180deg, rgba(255,255,255,.85), rgba(255,255,255,.55));
    }
    .hero{
      padding: 26px 22px 18px;
      border-bottom: 1px solid var(--edge);
      position: relative;
      overflow: hidden;
    }
    .hero:before{
      content:"";
      position:absolute;
      inset:-20% -20% auto -20%;
      height: 240px;
      background:
        radial-gradient(900px 220px at 20% 20%, rgba(125,211,252,.20), transparent 60%),
        radial-gradient(700px 220px at 82% 35%, rgba(167,139,250,.18), transparent 60%);
      filter: blur(0px);
      opacity:.95;
    }
    .hero > *{ position: relative; }

    .kicker{
      display:inline-flex;
      align-items:center;
      gap: 10px;
      font-family: var(--mono);
      letter-spacing: .3px;
      font-size: .78rem;
      color: var(--muted);
      padding: 7px 10px;
      border: 1px solid var(--edge);
      border-radius: 999px;
      background: rgba(255,255,255,.05);
    }
    .title{
      margin: 14px 0 8px 0;
      font-size: clamp(1.7rem, 3.3vw, 2.5rem);
      letter-spacing: -0.6px;
      line-height: 1.12;
      font-weight: 800;
      color: var(--ink);
    }
    .dek{
      margin: 0;
      color: var(--muted);
      max-width: 70ch;
      line-height: 1.45;
      font-weight: 560;
    }

    .content{
      padding: 22px;
    }
    .prose{
      max-width: var(--max);
      margin: 0 auto;
      font-family: var(--serif);
      font-size: var(--p);
      line-height: var(--leading);
      color: var(--ink);
      text-rendering: geometricPrecision;
      -webkit-font-smoothing: antialiased;
    }
    .prose p{
      margin: 0 0 1.05em 0;
    }
    .prose hr{
      border: none;
      border-top: 1px solid var(--edge);
      margin: 1.7em 0;
    }
    .prose h2{
      font-family: var(--sans);
      margin: 1.6em 0 .6em;
      font-size: 1.1rem;
      letter-spacing: .2px;
      color: var(--ink);
    }

    .dropcap:first-letter{
      float: left;
      font-family: var(--serif);
      font-size: 3.5em;
      line-height: .95;
      padding: .08em .10em 0 0;
      color: var(--ink);
      text-shadow: 0 10px 40px rgba(0,0,0,.25);
    }

    blockquote{
      margin: 1.2em auto;
      padding: 16px 18px;
      border-left: 3px solid rgba(125,211,252,.55);
      background: rgba(255,255,255,.05);
      border-radius: 14px;
      color: var(--ink);
      max-width: var(--max);
      box-shadow: 0 14px 40px rgba(0,0,0,.18);
    }
    :root[data-theme="light"] blockquote{ background: rgba(255,255,255,.75); }
    blockquote p{ margin: 0; }
    code{
      font-family: var(--mono);
      font-size: .92em;
      color: var(--ink);
      background: rgba(255,255,255,.06);
      padding: .12em .35em;
      border-radius: 10px;
      border: 1px solid var(--edge);
    }
    .callout{
      max-width: var(--max);
      margin: 1.4em auto;
      padding: 16px 16px;
      border-radius: 16px;
      border: 1px solid var(--edge);
      background:
        radial-gradient(900px 240px at 15% 0%, rgba(125,211,252,.15), transparent 55%),
        rgba(255,255,255,.04);
      color: var(--muted);
      font-family: var(--sans);
      box-shadow: 0 16px 50px rgba(0,0,0,.20);
    }
    :root[data-theme="light"] .callout{
      background:
        radial-gradient(900px 240px at 15% 0%, rgba(14,165,233,.14), transparent 55%),
        rgba(255,255,255,.65);
    }
    .callout strong{ color: var(--ink); }

    .footer{
      padding: 18px 22px 22px;
      border-top: 1px solid var(--edge);
      color: var(--muted);
      font-size: .9rem;
      display:flex;
      justify-content: space-between;
      gap: 14px;
      flex-wrap: wrap;
    }

    /* Print */
    @media print{
      header, .sidebar{ display: none !important; }
      body{ background: white !important; color: black !important; }
      article{
        box-shadow: none !important;
        border: none !important;
        background: white !important;
      }
      .hero{ border-bottom: 1px solid #ddd !important; }
      .prose{ color: #111 !important; }
    }
  </style>
</head>

<body>
  <header>
    <div class="topbar">
      <div class="brand" aria-label="Site brand">
        <div class="sigil" aria-hidden="true"></div>
        <div>
          <h1>The Mnemosyne Reading Room <span class="sub">Serein Habitat • Stack Nine</span></h1>
        </div>
      </div>

      <div class="controls" aria-label="Reader controls">
        <div class="pill" role="group" aria-label="Text size">
          <span class="label">Text</span>
          <input id="size" type="range" min="15" max="22" value="17" />
          <span class="label" id="sizeVal" aria-live="polite">17px</span>
        </div>

        <button class="btn" id="themeBtn" type="button" aria-label="Toggle theme">Toggle theme</button>
        <button class="btn" id="printBtn" type="button" aria-label="Print page">Print</button>
      </div>
    </div>
  </header>

  <main>
    <aside class="sidebar" aria-label="Sidebar">
      <h2 class="toc-title">On this page</h2>

      <div class="meta" aria-label="Story metadata">
        <div class="chiprow">
          <span class="chip">📚 Long-form</span>
          <span class="chip">🌀 Sci-fi / diplomatic</span>
          <span class="chip">🫧 Quiet tension</span>
        </div>
        <div class="chiprow">
          <span class="chip">⌛ Reading mode</span>
          <span class="chip">🔒 No recordings</span>
        </div>
      </div>

      <nav class="toc" aria-label="Table of contents">
        <a href="#story">
          The story
          <small>Full text, readable typography</small>
        </a>
        <a href="#selected-quote">
          Selected excerpt
          <small>A clean pull-quote block</small>
        </a>
        <a href="#endnote">
          Endnote
          <small>Return what you borrow</small>
        </a>
      </nav>
    </aside>

    <article id="story" aria-label="Story">
      <section class="hero">
        <span class="kicker">MNEMOSYNE COIL: LIVE • CONSENT INTERLOCK: READY</span>
        <h1 class="title">The Library of Borrowed Tomorrows</h1>
        <p class="dek">
          A reading page with warm, quiet UI—meant to feel like lamplight in a rotating habitat: present,
          gentle, and just sharp enough to matter.
        </p>
      </section>

      <section class="content">
        <div class="prose" id="prose">
          <p class="dropcap">
            The coil woke the way old machines always did—reluctant, dignified, and louder than they meant to be.
          </p>

          <p>
            A low hum rolled through the room, more felt than heard, as the superconducting ring in the center of the library’s smallest chamber drank its first measured mouthful of current. The sound threaded itself into the bones of the habitat, into the soft vibration of the rotating gardens far down the corridor, into the warm recycled air that never stopped moving and never quite stopped smelling of basil and copper.
          </p>

          <p>
            I rested my palm on the console’s edge and waited for the hum to find its steady pitch.
          </p>

          <p>
            There were protocols for everything on Serein Habitat—how to vent a pressure lock, how to comfort a stranger in the lift, how to return a borrowed book to the stacks without making the empty aisles feel accusatory. There were even protocols, now, for <em>borrowing tomorrow</em>.
          </p>

          <div class="callout" role="note" aria-label="Reading note">
            <strong>Tip:</strong> Use the slider in the header to adjust text size. Toggle theme for a brighter reading room. Print creates a clean, ink-friendly layout.
          </div>

          <p>
            The civic council, in its infinite talent for naming things as if they were already harmless, called the experiment <strong>The Mnemosyne Pilot</strong>.
          </p>

          <p>
            Everyone else called it the library.
          </p>

          <p>
            Not because the device looked like literature—though its ring did resemble the haloed spine of some vast, upright book—but because what it offered had the same shape as borrowing: you took something that wasn’t yours <em>yet</em>, you carried it home in your body, and you lived with the obligation to return it by eventually becoming the person who could remember it without flinching.
          </p>

          <hr />

          <p>
            One memory per consenting citizen. One. Legally sanctioned. Monitored. Logged only in the thinnest metadata—time of use, biometric safety, the binary fact that the machine had worked or refused to work. No recording of content. No screenshots. No transcripts. No souvenirs.
          </p>

          <p>
            A library, in other words, that could not keep a copy of its own books.
          </p>

          <p>
            I was the one tasked with making that possible.
          </p>

          <p>
            The council didn’t choose me because I was a neuroscientist—there were far better minds for that, and they had designed the ring years ago before the law caught up with the physics. The council chose me because I was an aging systems archivist, and systems archivists are paid to do three things well:
          </p>

          <p>
            1. Preserve what must not be lost.<br/>
            2. Delete what must not be kept.<br/>
            3. Know which is which when the difference is made of human skin.
          </p>

          <hr />

          <h2 id="selected-quote">Selected excerpt</h2>
          <blockquote>
            <p>
              Hope—borrowed early—was not a counterfeit. It was a library book checked out against your own name.
              You didn’t get to keep it. You got to live in a way that made returning it possible.
            </p>
          </blockquote>

          <h2 id="endnote">Endnote</h2>
          <p>
            If you want this page to contain the <em>entire</em> story text (verbatim), paste the full story into the
            <code>&lt;div class="prose"&gt;</code> section, replacing this shortened layout copy—everything is styled to handle long-form
            prose cleanly (paragraphs, headings, blockquotes, and callouts).
          </p>
        </div>
      </section>

      <footer class="footer" aria-label="Footer">
        <div>Return what you borrow.</div>
        <div style="font-family: var(--mono);">Stack Nine • Quiet Maintenance</div>
      </footer>
    </article>
  </main>

  <script>
    // Theme toggle (persisted)
    const root = document.documentElement;
    const themeBtn = document.getElementById('themeBtn');

    function setTheme(next){
      root.dataset.theme = next;
      localStorage.setItem('theme', next);
      themeBtn.textContent = next === 'light' ? 'Dark mode' : 'Light mode';
    }

    const savedTheme = localStorage.getItem('theme');
    if(savedTheme){ setTheme(savedTheme); }
    else{
      // Use system preference as a default
      const prefersLight = window.matchMedia && window.matchMedia('(prefers-color-scheme: light)').matches;
      setTheme(prefersLight ? 'light' : 'dark');
    }

    themeBtn.addEventListener('click', () => {
      const current = root.dataset.theme || 'dark';
      setTheme(current === 'dark' ? 'light' : 'dark');
    });

    // Text size slider
    const size = document.getElementById('size');
    const sizeVal = document.getElementById('sizeVal');
    const prose = document.getElementById('prose');

    function applySize(px){
      prose.style.setProperty('--p', px + 'px');
      sizeVal.textContent = px + 'px';
      localStorage.setItem('textSizePx', String(px));
    }

    const savedSize = localStorage.getItem('textSizePx');
    if(savedSize){
      const px = Math.min(22, Math.max(15, parseInt(savedSize, 10) || 17));
      size.value = String(px);
      applySize(px);
    } else {
      applySize(parseInt(size.value, 10));
    }

    size.addEventListener('input', (e) => applySize(parseInt(e.target.value, 10)));

    // Print
    document.getElementById('printBtn').addEventListener('click', () => window.print());
  </script>
</body>
</html>
