import re
from pathlib import Path
import PyPDF2

pdf_path = Path('/mnt/data/H1 Mathematics A Level Notes-edited.pdf')
reader = PyPDF2.PdfReader(str(pdf_path))

# regex to capture chapter headings with optional part in parentheses
chapter_re = re.compile(r'(Chapter\s*\d+\s*(?:\([^\)]*\))?\s*[:]?\s*[^\n\r]*?)\s*$', re.IGNORECASE)
# also handle 'Chapter X : Something' with spaces
chapter_re2 = re.compile(r'(Chapter\s*\d+\s*[:]\s*[^\n\r]*?)\s*$', re.IGNORECASE)

# We'll collect first occurrence of each normalized title
found = {}

for i, page in enumerate(reader.pages):
    try:
        text = page.extract_text() or ''
    except Exception:
        continue
    # consider only first 15 lines for headings
    lines = [ln.strip() for ln in text.splitlines()[:20] if ln.strip()]
    for ln in lines:
        m = chapter_re.search(ln)
        if not m:
            m = chapter_re2.search(ln)
        if m:
            raw = m.group(1)
            # normalize whitespace
            norm = re.sub(r'\s+', ' ', raw)
            # remove trailing page indicators like 'Page 1 of 54' etc if included
            norm = re.sub(r'\s+Page\s+\d+\s+of\s+\d+.*$', '', norm, flags=re.IGNORECASE)
            # if norm ends with just 'Chapter X' without title, skip
            if re.fullmatch(r'Chapter\s*\d+', norm, flags=re.IGNORECASE):
                continue
            if norm not in found:
                found[norm] = i+1

# Also capture major 'Check your Understanding' sections
check_re = re.compile(r'^Check your Understanding\s*\(([^\)]*)\)', re.IGNORECASE)
checks = {}
for i, page in enumerate(reader.pages):
    try:
        text = page.extract_text() or ''
    except Exception:
        continue
    lines = [ln.strip() for ln in text.splitlines()[:10] if ln.strip()]
    for ln in lines:
        m = check_re.match(ln)
        if m:
            topic = f"Check your Understanding ({m.group(1).strip()})"
            if topic not in checks:
                checks[topic] = i+1

print('Found chapters:', len(found))
for title, p in sorted(found.items(), key=lambda x: x[1]):
    print(f"{p:>4}  {title}")
print('\nFound check sections:', len(checks))
for title, p in sorted(checks.items(), key=lambda x: x[1]):
    print(f"{p:>4}  {title}")
PY
