\item (a) $756=2^2\times 3^3\times 7$\quad (b) $t=21$\quad (c) $u=98$
\item $p=70$;\quad $\sqrt{630p}=210$
\item $q=20$;\quad $\dfrac{540}{20}=27$
\item $x=3,\ y=2$
\item (a) HCF $=2^2\times 3\times 7$;\quad LCM $=2^2\times 3^3\times 7^2$\quad (b) $n=9$
\item (a) HCF $=420$, LCM $=2520$\quad (b) $k=35$\quad (c) $m=3^2\times 5^2\times 7^2$
\item $k=2^3\times 3^5\times 5^4\times 7^3$
\end{enumerate}

\subsection*{Section 4}

\begin{enumerate}[itemsep=0.35\baselineskip]
