\begin{document}

\begin{center}
{\Large Practice Exercise 1.3 --- Solutions}
\end{center}

\begin{enumerate}

% ------------------------------------------------------------
\item Express each as a single fraction in its simplest form.
\begin{enumerate}
\item
\begin{align*}
\frac1{2a}+\frac1{4a}
&=\frac2{4a}+\frac1{4a}
=\frac3{4a}.
\end{align*}

\item
\begin{align*}
\frac3{10w}+\frac2{15w}
&=\frac9{30w}+\frac4{30w}
=\frac{13}{30w}.
\end{align*}

\item
\begin{align*}
\frac5{2m}+\frac7{mn}
&=\frac{5n}{2mn}+\frac{14}{2mn}
=\frac{5n+14}{2mn}.
\end{align*}

\item
\begin{align*}
\frac{4a}{3x}+\frac{5x}{7a}
&=\frac{4a\cdot 7a}{21ax}+\frac{5x\cdot 3x}{21ax}
=\frac{28a^2+15x^2}{21ax}.
\end{align*}

\item
\begin{align*}
1+\frac{13}{2pq}
&=\frac{2pq}{2pq}+\frac{13}{2pq}
=\frac{2pq+13}{2pq}.
\end{align*}

\item
\begin{align*}
\frac9{xy}+\frac2{yz}
&=\frac{9z}{xyz}+\frac{2x}{xyz}
=\frac{2x+9z}{xyz}.
\end{align*}

\item
\begin{align*}
\frac3{uv}+\frac5{2u^2}
&=\frac{6u}{2u^2v}+\frac{5v}{2u^2v}
=\frac{6u+5v}{2u^2v}.
\end{align*}

\item
\begin{align*}
\frac7{2w}+\frac4{5w^2}
&=\frac{35w}{10w^2}+\frac8{10w^2}
=\frac{35w+8}{10w^2}.
\end{align*}

\item
\begin{align*}
\frac4{c^2d}+\frac9{cd^2}
&=\frac{4d}{c^2d^2}+\frac{9c}{c^2d^2}
=\frac{9c+4d}{c^2d^2}.
\end{align*}

\item
\begin{align*}
\frac{11}{6ab^3}+\frac{8}{21a^2b}
&=\frac{11(7a)}{42a^2b^3}+\frac{8(2b^2)}{42a^2b^3}
=\frac{77a+16b^2}{42a^2b^3}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item Express each of the following as a single fraction in its simplest form.
\begin{enumerate}
\item
\begin{align*}
\frac1{3w}-\frac1{12w}
&=\frac4{12w}-\frac1{12w}
=\frac3{12w}
=\frac1{4w}.
\end{align*}

\item
\begin{align*}
\frac4{21z}-\frac3{14z}
&=\frac8{42z}-\frac9{42z}
=-\frac1{42z}.
\end{align*}

\item
\begin{align*}
\frac9{2c}-\frac{10}{cr}
&=\frac{9r}{2cr}-\frac{20}{2cr}
=\frac{9r-20}{2cr}.
\end{align*}

\item
\begin{align*}
\frac pq-\frac rs
&=\frac{ps}{qs}-\frac{qr}{qs}
=\frac{ps-qr}{qs}.
\end{align*}

\item
\begin{align*}
5-\frac6{7pq}
&=\frac{35pq}{7pq}-\frac6{7pq}
=\frac{35pq-6}{7pq}.
\end{align*}

\item
\begin{align*}
\frac{2c}{5k}-\frac{5k}{2c}
&=\frac{4c^2}{10ck}-\frac{25k^2}{10ck}
=\frac{4c^2-25k^2}{10ck}.
\end{align*}

\item
\begin{align*}
\frac7{pq}-\frac6{qr}
&=\frac{7r}{pqr}-\frac{6p}{pqr}
=\frac{7r-6p}{pqr}.
\end{align*}

\item
\begin{align*}
\frac5{6xy^2}-\frac{13}{2x^3y}
&=\frac{5x^2}{6x^3y^2}-\frac{39y}{6x^3y^2}
=\frac{5x^2-39y}{6x^3y^2}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item Express each as a fraction with a single denominator.
\begin{enumerate}
\item
\begin{align*}
\frac2{d-3}-\frac3{8d}
&=\frac{16d}{8d(d-3)}-\frac{3(d-3)}{8d(d-3)}\\
&=\frac{16d-3d+9}{8d(d-3)}
=\frac{13d+9}{8d(d-3)}.
\end{align*}

\item
\begin{align*}
\frac1{w+2}+\frac1{w-2}
&=\frac{w-2}{(w+2)(w-2)}+\frac{w+2}{(w+2)(w-2)}\\
&=\frac{2w}{w^2-4}.
\end{align*}

\item
\begin{align*}
\frac3{p+7}-\frac2{p-8}
&=\frac{3(p-8)-2(p+7)}{(p+7)(p-8)}\\
&=\frac{3p-24-2p-14}{(p+7)(p-8)}
=\frac{p-38}{(p+7)(p-8)}.
\end{align*}

\item
\begin{align*}
\frac8{4x-3}-\frac4{x+2}
&=\frac{8(x+2)-4(4x-3)}{(4x-3)(x+2)}\\
&=\frac{8x+16-16x+12}{(4x-3)(x+2)}
=\frac{28-8x}{(4x-3)(x+2)}.
\end{align*}

\item
\begin{align*}
\frac5{4x-7}-\frac2{3x+1}
&=\frac{5(3x+1)-2(4x-7)}{(4x-7)(3x+1)}\\
&=\frac{15x+5-8x+14}{(4x-7)(3x+1)}
=\frac{7x+19}{(4x-7)(3x+1)}.
\end{align*}

\item
\begin{align*}
\frac7{t-9}-\frac3{5t-4}
&=\frac{7(5t-4)-3(t-9)}{(t-9)(5t-4)}\\
&=\frac{35t-28-3t+27}{(t-9)(5t-4)}
=\frac{32t-1}{(t-9)(5t-4)}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item Express each as a fraction with a single denominator.
\begin{enumerate}
\item
\begin{align*}
\frac{m}{m-n}-\frac{n+m}{n-m}
&=\frac{m}{m-n}-\frac{n+m}{-(m-n)}
=\frac{m}{m-n}+\frac{n+m}{m-n}\\
&=\frac{2m+n}{m-n}.
\end{align*}

\item
\begin{align*}
\frac9{2y-7}+\frac5{7-2y}
&=\frac9{2y-7}+\frac5{-(2y-7)}
=\frac{9-5}{2y-7}
=\frac4{2y-7}.
\end{align*}

\item
\begin{align*}
\frac{5w}{n-4v}-\frac{7w}{2n-8v}
&=\frac{5w}{n-4v}-\frac{7w}{2(n-4v)}
=\frac{10w-7w}{2(n-4v)}\\
&=\frac{3w}{2(n-4v)}.
\end{align*}

\item
\begin{align*}
\frac4{2a-b}-\frac7{3b-6a}
&=\frac4{2a-b}-\frac7{-3(2a-b)}
=\frac4{2a-b}+\frac7{3(2a-b)}\\
&=\frac{12+7}{3(2a-b)}
=\frac{19}{3(2a-b)}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item Express each as a fraction with a single denominator.
\begin{enumerate}
\item
\begin{align*}
\frac3{x^2-4}+\frac2{x-2}
&=\frac3{(x-2)(x+2)}+\frac{2(x+2)}{(x-2)(x+2)}\\
&=\frac{3+2x+4}{x^2-4}
=\frac{2x+7}{x^2-4}.
\end{align*}

\item
\begin{align*}
\frac7{x+3}-\frac4{x^2-9}
&=\frac{7(x-3)}{(x-3)(x+3)}-\frac4{(x-3)(x+3)}\\
&=\frac{7x-21-4}{x^2-9}
=\frac{7x-25}{x^2-9}.
\end{align*}

\item
\begin{align*}
\frac1{x^2-49}+\frac6{7-x}
&=\frac1{(x-7)(x+7)}-\frac6{x-7}\\
&=\frac1{x^2-49}-\frac{6(x+7)}{x^2-49}
=\frac{-6x-41}{x^2-49}.
\end{align*}

\item
\begin{align*}
\frac3{5x-1}-\frac4{25x^2-1}
&=\frac{3(5x+1)}{(5x-1)(5x+1)}-\frac4{(5x-1)(5x+1)}\\
&=\frac{15x+3-4}{25x^2-1}
=\frac{15x-1}{25x^2-1}.
\end{align*}

\item
\begin{align*}
\frac4{(y+2)^2}+\frac1{y+2}
&=\frac4{(y+2)^2}+\frac{y+2}{(y+2)^2}
=\frac{y+6}{(y+2)^2}.
\end{align*}

\item
\begin{align*}
\frac7{r-9}-\frac2{(r-9)^2}
&=\frac{7(r-9)}{(r-9)^2}-\frac2{(r-9)^2}
=\frac{7r-63-2}{(r-9)^2}\\
&=\frac{7r-65}{(r-9)^2}.
\end{align*}

\item
\begin{align*}
\frac5{w^2-2w+1}-\frac7{w-1}
&=\frac5{(w-1)^2}-\frac{7(w-1)}{(w-1)^2}\\
&=\frac{5-7w+7}{(w-1)^2}
=\frac{12-7w}{(w-1)^2}.
\end{align*}

\item
\begin{align*}
\frac4{z+5}+\frac3{z^2+10z+25}
&=\frac{4(z+5)}{(z+5)^2}+\frac3{(z+5)^2}
=\frac{4z+20+3}{(z+5)^2}\\
&=\frac{4z+23}{(z+5)^2}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item Farrah simplifies $\dfrac{2}{x-2}+\dfrac{1}{x^2-4}$. Correct working:
\begin{align*}
\frac{2}{x-2}+\frac{1}{x^2-4}
&=\frac{2}{x-2}+\frac{1}{(x-2)(x+2)}\\
&=\frac{2(x+2)}{(x-2)(x+2)}+\frac{1}{(x-2)(x+2)}\\
&=\frac{2(x+2)+1}{(x-2)(x+2)}
=\frac{2x+5}{x^2-4}.
\end{align*}

% ------------------------------------------------------------
\item Express each as a single fraction in its simplest form.
\begin{enumerate}
\item
\begin{align*}
\frac4{x+4}-\frac{2x}{x^2-16}-\frac3{4-x}
&=\frac4{x+4}-\frac{2x}{(x-4)(x+4)}+\frac3{x-4}\\
&=\frac{4(x-4)}{x^2-16}-\frac{2x}{x^2-16}+\frac{3(x+4)}{x^2-16}\\
&=\frac{4x-16-2x+3x+12}{x^2-16}
=\frac{5x-4}{x^2-16}.
\end{align*}

\item
\begin{align*}
\frac2{q^2+4q+4}-\frac1{q^2-4}
&=\frac2{(q+2)^2}-\frac1{(q-2)(q+2)}\\
&=\frac{2(q-2)}{(q+2)^2(q-2)}-\frac{q+2}{(q+2)^2(q-2)}\\
&=\frac{2q-4-q-2}{(q+2)^2(q-2)}
=\frac{q-6}{(q+2)^2(q-2)}.
\end{align*}
\end{enumerate}

% ------------------------------------------------------------
\item The distance between park $A$ and park $B$ is $3$ km. Total time (hours):
\begin{align*}
\frac{3}{x}+\frac14+\frac{3}{x-2}
&=\frac{12(x-2)}{4x(x-2)}+\frac{x(x-2)}{4x(x-2)}+\frac{12x}{4x(x-2)}\\
&=\frac{12x-24+x^2-2x+12x}{4x(x-2)}
=\frac{x^2+22x-24}{4x(x-2)}.
\end{align*}

% ------------------------------------------------------------
\item Tap $P$ fills a tank in $x$ minutes; tap $Q$ takes $x+3$ minutes.
\begin{enumerate}
\item
\begin{align*}
\text{(i)}\quad &\frac{1}{x}\ \text{tank/min},\\
\text{(ii)}\quad &\frac{1}{x+3}\ \text{tank/min}.
\end{align*}

\item
\begin{align*}
\text{Rate} &= \frac1x+\frac1{x+3}
=\frac{x+3+x}{x(x+3)}
=\frac{2x+3}{x(x+3)},\\
\text{Time} &= \left(\frac{2x+3}{x(x+3)}\right)^{-1}
=\frac{x(x+3)}{2x+3}\ \text{minutes}.
\end{align*}
\end{enumerate}

\end{enumerate}

