def rounded_sum(votes, dp=1):
    total=sum(votes)
    perc=[round(v/total*100, dp) for v in votes]
    return perc, sum(perc)
candidates=[]
for a in range(50,200):
    for b in range(50,200):
        for c in range(50,200):
            total=a+b+c
            if total in (300,301,299,400,257,259):
                perc, s=rounded_sum([a,b,c],1)
                if abs(s-100)>0.05: # ensure not 100.0
                    candidates.append((a,b,c,total,perc,s))
len(candidates), candidates[:10]

